import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  setTextFilter,
  sortByDate,
  sortByAmount,
  setStartDate,
  setEndDate,
} from '../actions/filters';
import 'react-dates/initialize';
import { DateRangePicker } from 'react-dates';
import 'react-dates/lib/css/_datepicker.css';

const ExpenseListFilter = () => {
  const dispatch = useDispatch();

  // SELECTORS
  const filters = useSelector((state) => state.filters);

  // STATE
  const [calenderFocused, setCalenderFocused] = useState(null);

  // state = {
  //   calenderFocused: null
  // };
  const onDatesChangeHandler = ({ startDate, endDate }) => {
    dispatch(setStartDate(startDate));
    dispatch(setEndDate(endDate));
  };

  const onFocusChangeHandler = (calenderFocused) => {
    this.setState(() => ({ calenderFocused }));
  };

  return (
    <div className='content-container'>
      <div className='input-group'>
        <div className='input-group__item'>
          <input
            type='text'
            className='text-input'
            placeholder='Search expenses'
            value={this.props.filters.text}
            onChange={(e) => {
              this.props.dispatch(setTextFilter(e.target.value));
            }}
          />
        </div>
        <div className='input-group__item'>
          <select
            className='select'
            value={this.props.filters.sortBy}
            onChange={(e) => {
              if (e.target.value === 'date') this.props.dispatch(sortByDate());
              else if (e.target.value === 'amount')
                this.props.dispatch(sortByAmount());
            }}
          >
            <option value='date'>Date</option>
            <option value='amount'>Amount</option>
          </select>
        </div>
        <div className='input-group__item'>
          <DateRangePicker
            startDate={this.props.filters.startDate}
            startDateId='startDateId'
            endDate={this.props.filters.endDate}
            endDateId='endDateId'
            onDatesChange={this.onDatesChange}
            focusedInput={this.state.calenderFocused}
            onFocusChange={this.onFocusChange}
            numberOfMonths={1}
            isOutsideRange={() => false}
            showClearDates={true}
          />
        </div>
      </div>
    </div>
  );
};

export default ExpenseListFilter;
