import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import moment from 'moment';
import 'react-dates/initialize';
import 'react-dates/lib/css/_datepicker.css';
import { SingleDatePicker } from 'react-dates';

const ExpenseForm = ({ onSubmit }) => {
  // SELECTORS
  const expense = useSelector((state) => state.expenses);

  // STATE
  const [description, setDescription] = useState(
    expense ? expense.description : ''
  );
  const [note, setNote] = useState(expense ? expense.note : '');
  const [amount, setAmount] = useState(
    expense ? (expense.amount / 100).toString() : ''
  );
  const [createdAt, setCreatedAt] = useState(
    expense ? moment(expense.createdAt) : moment()
  );
  const [calenderFocused, setCalenderFocused] = useState(false);

  // HANDLERS
  const onAmountChangeHandler = (e) => {
    if (!amount || amount.match(/^\d{1,}(\.\d{0,2})?$/)) 
      setAmount(e.target.value);
  };

  const onDateChangeHandler = (createdAt) => {
    if (createdAt) setCreatedAt(createdAt);
  };

  // const onFocusChangeHandler = ({ focused }) => {
  //   setCalenderFocused(focused);
  // };
  const onFocusChangeHandler = () => {
    setCalenderFocused(!calenderFocused);
  };

  const onSubmitHandler = (e) => {
    e.preventDefault();
    // Send data upstream
    onSubmit({
      description: description,
      amount: parseFloat(amount, 10) * 100,
      createdAt: createdAt.valueOf(),
      note: note,
    });
  };

  return (
    <form className='form' onSubmit={onSubmitHandler}>
      <input
        type='text'
        placeholder='Description'
        autoFocus
        className='text-input'
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        required
      />
      <input
        type='text'
        placeholder='Amount'
        className='text-input'
        value={amount}
        onChange={(e) => onAmountChangeHandler(e)}
        required
      />
      <SingleDatePicker
        date={createdAt}
        onDateChange={() => onDateChangeHandler(createdAt)}
        focused={calenderFocused}
        onFocusChange={onFocusChangeHandler}
        numberOfMonths={1}
        readOnly={true}
        isOutsideRange={() => false}
      />
      <textarea
        placeholder='Add a note'
        className='textarea'
        value={note}
        onChange={(e) => setNote(e.target.value)}
      ></textarea>
      <button className='button'>Add Expense</button>
    </form>
  );
};

export default ExpenseForm;
